import React from 'react';
import TodoList from "./components/TodoList";
import TodoForm from './components/TodoForm';

const todoData = [
  {
    task: 'Organize Garage',
    id: 1528817077286,
    completed: false
  },
  {
    task: 'Bake Cookies',
    id: 1528817084358,
    completed: false
  }
];

class App extends React.Component {
  constructor(){
    super();
    this.state = {
      todos: todoData,
      todo: ''
    };
  }
//for setting what user inputs in the TodoForm to state 
  handleChange=(e)=>{
    this.setState({ [e.target.name]: e.target.value })
  }

 addTodo=(e) => {
   e.preventDefault();
   const newTodo = { task: this.state.todo, completed: false, id: Date.now() };
   this.updateLocalStorage();
   
    this.setState({ 
      todos: [...this.state.todos, newTodo], 
      todo: '' 
    });
    
 }


  clearCompleted = e => {
    e.preventDefault();
    let todos = this.state.todos.filter(todo => !todo.completed);
    this.setState({todos});
  }

  toggleCompleted = (id) => {
    this.setState({
      todos: this.state.todos.map(todo => {
        if (id === todo.id){
          return {...todo, completed: !todo.completed};
        } else {
          return todo;
        }
      })
    });
  }
  componentDidMount (){
    this.setLocalStorageToState
  }
  updateLocalStorage = () => {
    console.log("update", this.state.todos);
    localStorage.setItem("todos", JSON.stringify(this.state.todos))
  };
  
  setLocalStorageToState = () => {
    console.log("update 2", this.state.todos);
    this.state.todos && this.setState({
          todos: JSON.parse(localStorage.getItem("todos"))
        })
      }
 
  render() {
    return (
      <div>
        <h2>Welcome to your Todo App!</h2>
        <TodoList todos={this.state.todos} toggleCompleted={this.toggleCompleted} />
        <TodoForm 
          value={this.state.todo}
          handleChange={this.handleChange}
          handleOnSubmit={this.addTodo}
          clearCompleted={this.clearCompleted}
        />
      </div>
    );
  }
}

export default App;
